package ha_utils

import (
	"bytes"
	"io/ioutil"
	"net/http"
	neturl "net/url"

	log "github.com/Sirupsen/logrus"
	"github.com/stretchr/testify/require"
)

func AddValidLicense(t require.TestingT, serverURL, username, password string) {
	log.Infof("Attempting to add license on %s", serverURL)
	orcaURL, err := neturl.Parse(serverURL)
	require.Nil(t, err)
	dclient, err := GetUCPUserDockerClient(serverURL, username, password)
	require.Nil(t, err)
	client := dclient.HTTPClient

	orcaURL.Path = "/api/config/license"
	// WARNING! this is real license, don't let it leak into the field
	data := []byte(
		`{"auto_refresh":true,"license_config":{"key_id":"4Hg5DGMH78wN5ZjNjbau_agErRqNE5aQ-R3MnUiNYdGg","private_key":"y2dhoSi4jAYKpclaXnmd1R_RJkYy7ySmKCis9e1JpfH6","authorization":""}}`)
	req, err := http.NewRequest("POST", orcaURL.String(), bytes.NewBuffer(data))
	require.Nil(t, err)
	resp, err := client.Do(req)
	require.Nil(t, err)
	body, err := ioutil.ReadAll(resp.Body)
	resp.Body.Close()
	if resp.StatusCode > 299 || resp.StatusCode < 200 {
		require.FailNow(t, string(body))
	}
	log.Info("Succesfully added license")
}
