package util

import (
	"encoding/json"

	"github.com/docker/dhe-deploy/hubconfig"
)

func GetOfflineLicense() (*hubconfig.LicenseConfig, error) {
	offlineLicense := `{"key_id":"0zFZBPayEwn9R05Ug4pBmz4ud1Vwg9ijZoxqNl4S3ZTh","private_key":"TRa40quQF7XQFknxxl0Ev-PcegWFM0BkJq4b0AYA_lce","authorization":""}`
	// turn JSON encoded license string into a struct
	var licenseConfig *hubconfig.LicenseConfig
	if err := json.Unmarshal([]byte(offlineLicense), &licenseConfig); err != nil {
		return nil, err
	}

	return licenseConfig, nil
}

